import React, { Component } from "react";

class Edit extends Component {
  constructor() {
    super();

    this.state = {
      information: [{ firstName: "He" }, { lastName: "e" }, { email: "www" }]
    };
  }
  handleChange = event =>
    this.setState({
      [event.target.name]: event.target.value
    });
  editIformation = () => {
    let copyofInformation = this.state.information;

    var editText = prompt(
      "TO EDIT YOUR TEXT: ",
      copyofInformation[0].firstName
    );

    // if the user didnt click on cancel or didnt insert empty value
    if (editText !== null && editText !== "") {
      // take this text and put it in the name of this object in the copy array
      copyofInformation[0].firstName = editText;

      // change the trips that in the stete with the copy array
      this.setState({
        information: copyofInformation
      });
    }
  };

  render() {
    return (
      <div>
        <p>FirstName:{this.state.information[0].firstName}</p>
        <p>LastName: {this.state.information[0].lastName} </p>
        <p>Mail: {this.state.information[0].email} </p>
        <button
          type="button"
          class="btn btn-default btn-sm"
          onClick={() => this.editIformation()}
        />
      </div>
    );
  }
}
export default Edit;
